mapper.select:


SELECT * 
	FROM (SELECT A.*,
		     @ROWNUM := @ROWNUM + 1 AS RN
		     FROM (SELECT *
			  	FROM COMMENT
				WHERE COMMENT_MN = #{movie_koficCd} 
				ORDER BY COMMENT_DATE DESC) A, (SELECT @ROWNUM := 0) D
	)B
WHERE RN > (#{page}-1) * #{amount} AND RN <= #{page} * #{amount}

select count(*) as total
from COMMENT
where comment_mn = #{movie_koficCd}
