mapper.insert:
insert into QA(
				user_id,
				qa_title,
				qa_content
				)
values(
		#{user_id},
		#{qa_title},
		#{qa_content}
		)

mapper.select:
SELECT * FROM (SELECT A.*, @ROWNUM := @ROWNUM + 1 AS RN FROM (SELECT * FROM USER WHERE user_admin = 0
and (user_id like CONCAT('%', #{searchName},'%') or user_email like CONCAT('%', #{searchName},'%'))
and user_email like CONCAT('%', #{searchName},'%')
and user_id like CONCAT('%', #{searchName},'%')
ORDER BY USER_date DESC)A, (SELECT @ROWNUM := 0) D
)B WHERE rn > (#{page}-1) * #{amount} AND rn <= #{page} * #{amount}

mapper.update:
    	update USER

    	set user_email = #{user_newemail}


    	set user_password = #{user_newpassword}


    	set user_phone = #{user_newphone}


    	set user_birth = #{user_newbirth}

		where user_id = #{user_id}

update QA
set qa_comment = #{qa_comment}
where qa_key = #{qa_key}

update USER
set user_auth = true
where user_id = #{user_id}
